import React from 'react'
import './StyleSheet.css'
import { AgGridReact } from 'ag-grid-react'
import 'ag-grid-community/styles/ag-grid.css'
import 'ag-grid-community/styles/ag-theme-quartz.css'
import { FTOFeatureListHeader } from './TableProps'
import { FTOFeatureData } from './DefaultProps'
import PropTypes from 'prop-types'
import { SimpleToggle } from '../../../CommonComponents/SimpleToggle/SimpleToggle'
import {
    getMenuIconClass,
    getSerialRowNumber,
    isValidData,
} from '../CommonMethods'
import Icon from '../../../../IconLibrary/Icon'
import DuoIcon from '../../../../IconLibrary/DuoIcon'
import MyCheckbox from '../../../CommonComponents/BasicComponents/Checkbox/MyCheckbox'
import {
    AssigneeList,
    ComparisonInputs,
    CPCList,
    FamilyApplicantList,
    InfringedFeatureList,
    LegalStatus,
    PatentInfoWithCondition,
    PatentList,
    RelevantPatentInfo,
    SimilarityScore,
    SimpleList,
    PatentLink,
    ClassList,
    BooleanList,
} from './CustomTableCell'

const autoSizeStrategy = {
    type: 'fitGridWidth',
    defaultMinWidth: 100,
}

export default class MyTable extends React.Component {
    constructor(props) {
        super(props)
        this.tableContainerRef = React.createRef()
        this.state = {
            columnDefs: [],
            rowData: [],
            defaultColDef: {
                flex: 1,
                wrapText: true,
                autoHeight: true,
                maxHeight: 40,
                pinned: 'right',
                enableRenameColumns: true,

                // cellRenderer: AnyGenTableCell,
                // cellRendererParams: {
                //     maxCellHeight: this.props.maxCellHeight,
                // },
            },
        }
    }

    componentDidMount() {
        let {
            Headers,
            actionList,
            onAction,
            onChange,
            onViewDetails,
            selectAllInfo = {},
            pendingLoading = false,
            pendingHeaders = [],
        } = this.props
        this.prepareData(
            Headers,
            actionList,
            onAction,
            onChange,
            onViewDetails,
            selectAllInfo,
            pendingLoading,
            pendingHeaders
        )
        this.prepareTableHeight()
    }

    componentWillReceiveProps(nextProps, nextContext) {
        let {
            Headers,
            actionList,
            onAction,
            onChange,
            onViewDetails,
            selectAllInfo = {},
            pendingLoading = false,
            pendingHeaders = [],
        } = nextProps
        this.prepareData(
            Headers,
            actionList,
            onAction,
            onChange,
            onViewDetails,
            selectAllInfo,
            pendingLoading,
            pendingHeaders
        )
        this.prepareTableHeight()
    }

    prepareData = (
        Headers,
        actionList = [],
        onAction,
        onChange,
        onViewDetails,
        selectAllInfo = {},
        pendingLoading,
        pendingHeaders
    ) => {
        let headerArr = []
        for (let i = 0; i < Headers.length; i++) {
            let dataLoading = false
            if (
                pendingLoading === true &&
                pendingHeaders.includes(Headers[i].Key)
            ) {
                dataLoading = true
            }
            let DesignProps = Headers[i].Design
            let newHeaderInfo = {
                headerName: Headers[i].DisplayName,
                field: Headers[i].Key,
                sortable: Headers[i].Type === 'text',
                filter: Headers[i].Type === 'text' ? 'agSetColumnFilter' : '',
                pinned: DesignProps.Pinned,
                lockPinned: DesignProps.LockPinned,
                flex: DesignProps.Flex,
                minWidth: DesignProps.MinWidth,
                maxWidth:
                    DesignProps.MaxWidth === 'auto'
                        ? 1200
                        : DesignProps.MaxWidth,
                wrapText: true,
                resizable: DesignProps.Resizable,
                cellEditor: 'agCheckboxCellEditor',
                checkboxSelection: DesignProps.Selectable,
                showDisabledCheckboxes: true,
                enableSorting: Headers[i].Type === 'text',
                cellRenderer:
                    // Headers[i].Type === "checkbox" ? "agCheckboxCellRenderer" :
                    dataLoading === true
                        ? AnyGenLoadingCell
                        : Headers[i].Type === 'checkbox'
                          ? CheckboxCell
                          : Headers[i].Type === 'action'
                            ? ActionCell
                            : Headers[i].Type === 'toggle'
                              ? ToggleButtonCell
                              : Headers[i].Type === 'text'
                                ? AnyGenTableCell
                                : Headers[i].Type === 'number'
                                  ? AnyGenTableNumberCell
                                  : Headers[i].Type === 'custom'
                                    ? (params) =>
                                          AnyGenTableCustomCell(
                                              params,
                                              onViewDetails
                                          )
                                    : StatusCell,
                cellRendererParams: {
                    maxCellHeight: this.props.maxCellHeight,
                    onAction:
                        Headers[i].Type === 'checkbox' ? onChange : onAction,
                    actionList: actionList,
                    customCell: DesignProps.CustomCellDesign,
                    checkbox: DesignProps.Selectable,
                    parentFunction: isValidData(DesignProps.ParentFunctionName)
                        ? this.props[DesignProps.ParentFunctionName]
                        : () => {},
                },
                // headerComponent: Headers[i].Key === "checkbox"?CheckboxCell:AnyGenHeaderCell,
            }
            if (Headers[i].Type === 'checkbox') {
                newHeaderInfo.headerComponent = HeaderCheckboxCell
                newHeaderInfo.headerComponentParams = selectAllInfo
            } else if (Headers[i].Key === 'no') {
                let pageNumber = 1
                let pageSize = this.props.Data.length
                newHeaderInfo.valueGetter = (params) => {
                    if (isValidData(pageNumber) && isValidData(pageSize)) {
                        return getSerialRowNumber(
                            pageNumber,
                            pageSize,
                            params.node.rowIndex
                        )
                    } else {
                        return params.node.rowIndex + 1
                    }
                }
            }
            headerArr.push(newHeaderInfo)
        }
        this.setState({
            columnDefs: headerArr,
        })
    }

    prepareTableHeight = () => {
        let { cellHeight } = this.props
        let tableContainerHeight = this.tableContainerRef.current.clientHeight
        let heightType // autoHeight, manualHeight, fixedHeight
        let tableRowHeight = cellHeight
        let tableHeaderHeight = 50 //55.78
        let minHeight = 400 // 150 + 56
        let dataHeight =
            this.props.Data.length * tableRowHeight + tableHeaderHeight
        if (dataHeight < minHeight) {
            heightType = 'manualHeight'
            this.setState({
                tableHeight: dataHeight,
                heightType,
            })
        } else {
            heightType = 'fixedHeight'
            this.setState({
                tableHeight:
                    tableContainerHeight > 0 ? tableContainerHeight : 400,
                heightType,
            })
        }
        // console.log(this.tableContainerRef.current)
        console.log(dataHeight)
        console.log(tableRowHeight)
        console.log(heightType)
    }

    render() {
        let { autoHeight, tableHeight } = this.state
        let { noShadow } = this.props
        return (
            <div
                className={
                    noShadow
                        ? 'default-table-container no-shadow'
                        : 'default-table-container'
                }
                ref={this.tableContainerRef}
            >
                <div
                    className="ag-theme-quartz"
                    style={{
                        width: '100%',
                        height: tableHeight,
                    }}
                >
                    <AgGridReact
                        suppressRowClickSelection={false} // for row selection
                        suppressCellFocus={true} // for column color (selection)
                        domLayout={autoHeight ? 'autoHeight' : 'normal'}
                        defaultColDef={this.state.defaultColDef}
                        columnDefs={this.state.columnDefs}
                        rowClass={'my-table-row'}
                        autoSizeStrategy={autoSizeStrategy}
                        rowSelection={'multiple'}
