import React, { Component } from 'react'
import MyTable from '../BasicComponents/PopupTable/MyTable'
import Icon from '../../../IconLibrary/Icon'
import './AdditionalData.css'
import { Headers } from './TableProps'
import { Data } from './DefaultProps'
import { DataFileUpload } from './Data'

class AdditionalData extends Component {
    constructor(props) {
        super(props)

        this.state = {
            sortType: 'asc',
            selectedDate: '12/11/2023',
            dataFileUpload: [],
        }
    }

    componentDidMount() {
        this.setState({
            dataFileUpload: DataFileUpload,
        })
        console.log(DataFileUpload)
    }

    handleRadioChange = (event) => {
        this.setState({ selectedDate: event.target.value })
    }

    handleSortChange = (event) => {
        this.setState({
            sortType: this.state.sortType === 'asc' ? 'desc' : 'asc',
        })
    }

    render() {
        const { sortType, selectedDate, dataFileUpload } = this.state
        const dates = [
            { date: '12/11/2023', time: '12:30 PM' },
            { date: '12/10/2023', time: '12:30 PM' },
            { date: '12/09/2023', time: '12:30 PM' },
        ]

        return (
            <div className="additional-data">
                {dataFileUpload.length > 1 && (
                    <div className="data-upload-history">
                        <div className="data-upload-header">
                            <span className="data-upload-header-title">
                                Data Uploading Date
                            </span>
                            <div className="data-upload-header-action">
                                <button
                                    className="data-upload-header-action"
                                    onClick={this.handleSortChange}
                                >
                                    <Icon
                                        icon={
                                            sortType === 'asc'
                                                ? 'sort_down1'
                                                : 'sort_up1'
                                        }
                                    />
                                </button>
                            </div>
                        </div>
                        <div className="data-upload-history-list">
                            {dates.map((item, index) => (
                                <label
                                    key={index}
                                    className={`radio-item ${selectedDate === item.date ? 'selected' : ''}`}
                                >
                                    <input
                                        type="radio"
                                        value={item.date}
                                        checked={selectedDate === item.date}
                                        onChange={this.handleRadioChange}
                                    />
                                    <span>{item.date}</span>
                                    <span>{item.time}</span>
                                </label>
                            ))}
                        </div>
                    </div>
                )}

                <div className="additional-data-container">
                    <div className="additional-data-content">
                        <p className="additional-title">Upload Files</p>
                        <div className="additional-body">
                            <div className="file-tag-container">
                                <div className="file-tag-name">
                                    <Icon
                                        icon={'outline_importFile'}
                                        title="Uploaded Files"
                                    />
                                    <p className="file-name">
                                        {/*{dataFileUpload[0].fileName}*/}
                                    </p>
                                    <div className="file-size">55 KB</div>
                                </div>
                                <div className="file-tag-info">
                                    <Icon icon={'batch'} title="Row Count" />
                                    <span className="file-row-count">
                                        12,000
                                    </span>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="additional-data-content">
                        <p className="additional-title">Headers</p>
                        <div className="additional-body">
                            <MyTable Headers={Headers} Data={Data} />
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default AdditionalData
