import React, { Component } from 'react'
import TabPopup from '../BasicComponents/Tab/TabPopup'
import Nodes from '../Nodes/Nodes'
import './ClassificationTree.css'
import NodesActions from '../Nodes/NodesActions/NodesActions'

import { Headers } from '../Nodes/TableProps'
import { Data } from '../Nodes/DefaultProps'

class ClassificationTree extends Component {
    constructor(props) {
        super(props)
        this.state = {
            filterData: Headers,
            filteredData: Data,
        }
    }

    handleSearch = (event) => {
        const searchTerm = event.target.value.toLowerCase()

        if (this.state.typingTimeOut) {
            clearTimeout(this.state.typingTimeOut)
        }

        this.setState({
            searchValue: event.target.value,
            typingTimeOut: setTimeout(() => {
                const filteredData = this.state.data.filter((item) => {
                    // Check for search term in id.PatentId, title, abstract, and class.Name
                    const patentIdMatch =
                        item.id.PatentId.toLowerCase().includes(searchTerm)
                    const titleMatch = item.title
                        .toLowerCase()
                        .includes(searchTerm)
                    const abstractMatch = item.abstract
                        .toLowerCase()
                        .includes(searchTerm)
                    const classMatch = item.class.some((c) =>
                        c.Name.toLowerCase().includes(searchTerm)
                    )

                    return (
                        patentIdMatch ||
                        titleMatch ||
                        abstractMatch ||
                        classMatch
                    )
                })

                this.setState({ filteredData })
                this.handlePageChange(1)
            }, 500),
        })
    }

    render() {
        const tabs = [
            {
                title: 'Nodes',
                element: <Nodes filteredData={this.state.filteredData} />,
            },
            {
                title: 'Tree',
                element: <></>,
            },
        ]
        return (
            <div className="container">
                <TabPopup
                    tabs={tabs}
                    actions={
                        <NodesActions filterData={this.state.filterData} />
                    }
                />
            </div>
        )
    }
}

export default ClassificationTree
