import React, { Component } from 'react'
// import HeaderPopup from '../Header/HeaderPopup'
import { TabContext, TabPanel, TabList } from '@mui/lab'
import { Button, Box, Tab, Tabs } from '@mui/material'
import './Help.css'
import ReactPlayer from 'react-player'
import HeaderPopup from '../Header/HeaderPopup'

class Help extends Component {
    constructor(props) {
        super(props)
        this.state = {
            value: '1',
            documentation: {
                heading: 'Creating a Classification tree',
                steps: [
                    {
                        title: 'Start with the Root Node',
                        description:
                            'Drag and drop the root node onto the canvas.',
                    },
                    {
                        title: 'Add Child Nodes to the Root Node',
                        description:
                            'Drag and drop a node from the left side onto the root node to create a child node.',
                    },
                    {
                        title: 'Add Child Nodes to Existing Child Nodes',
                        description:
                            'Drag and drop a node from the left side onto an existing child node to create a child of that node.',
                    },
                    {
                        title: 'Change Parent Nodes',
                        description:
                            'If you want to change the parent node of a child node, click on the edge of the line connecting the child to its current parent, then drag it to the new parent node you want to connect it to.',
                    },
                    {
                        title: 'Repeat the Process',
                        description:
                            'Continue dragging and dropping nodes to build out your classification tree.',
                    },
                ],
            },
            videoUrl: 'https://youtu.be/HAj_d6dR_xM',
        }
    }
    render() {
        const handleChange = (event, newValue) => {
            this.setState({ ...this.state, value: newValue })
        }
        return (
            <>
                <HeaderPopup />
                <Box
                    sx={{
                        typography: 'body1',
                        padding: '38px',
                        maxWidth: '887px',
                        minWidth: '887px',
                    }}
                >
                    <TabContext value={this.state.value}>
                        <Box
                            sx={{
                                borderBottom: 1,
                                borderColor: 'divider',
                                display: 'flex',
                                justifyContent: 'space-between',
                            }}
                        >
                            <TabList
                                onChange={handleChange}
                                TabIndicatorProps={{
                                    style: {
                                        backgroundColor: '#000',
                                    },
                                }}
                                sx={{
                                    fontSize: '14px',
                                    '& button': {
                                        minWidth: '200px',
                                        textTransform: 'none',
                                    },
                                    '& button.Mui-selected': {
                                        color: 'black',
                                        fontWeight: 700,
                                    },
                                }}
                            >
                                <Tab label="Documentation" value="1" />
                                <Tab label="Video" value="2" />
                            </TabList>
                            <Button className="guide-btn" variant="contained">
                                Guide me
                            </Button>
                        </Box>
                        <TabPanel value="1" sx={{ padding: '0' }}>
                            {this.state.documentation && (
                                <>
                                    <h2 className="heading">
                                        {this.state.documentation.heading}
                                    </h2>
                                    {this.state.documentation.steps.map(
                                        (step, index) => (
                                            <div
                                                key={index}
                                                className="instruction-container"
                                            >
                                                <div>
                                                    <p className="title">
                                                        {index + 1}.
                                                    </p>
                                                </div>
                                                <div className="instruction-content">
                                                    <p className="title">
                                                        {step.title}
                                                    </p>
                                                    <p className="description">
                                                        {step.description}
                                                    </p>
                                                </div>
                                            </div>
                                        )
                                    )}
                                </>
                            )}
                        </TabPanel>
                        <TabPanel value="2">
                            <ReactPlayer
                                url={this.state.videoUrl}
                                controls={true}
                            />
                        </TabPanel>
                    </TabContext>
                </Box>
            </>
        )
    }
}

export default Help
