import React, { Component } from 'react'

import { Box, IconButton, Slider, Typography } from '@mui/material'

import Icon from '../../../IconLibrary/Icon'

import './VideoPlayer.css'

class VideoPlayer extends Component {
    constructor(props) {
        super(props)
        this.state = {
            videoUrl: 'https://www.w3schools.com/html/mov_bbb.mp4',
            isPlaying: true,
            volume: 50,
            currentTime: 0,
            duration: 0,
            muted: false,
            seeking: false,
        }
        this.videoRef = React.createRef()
    }

    componentDidMount() {
        this.videoRef.current.addEventListener(
            'loadedmetadata',
            this.handleLoadDuration
        )
        this.videoRef.current.addEventListener(
            'timeupdate',
            this.handleTimeUpdate
        )
    }

    componentWillUnmount() {
        this.videoRef.current.removeEventListener(
            'loadedmetadata',
            this.handleLoadDuration
        )
        this.videoRef.current.removeEventListener(
            'timeupdate',
            this.handleTimeUpdate
        )
    }

    handleLoadDuration = () => {
        this.setState({ duration: this.videoRef.current.duration })
    }

    handleTimeUpdate = () => {
        if (!this.state.seeking) return
        if (this.state.currentTime === this.state.duration) {
            this.setState({ isPlaying: false })
        }
        this.setState({ currentTime: this.videoRef.current.currentTime })
    }

    handleSeekChange = (event, newValue) => {
        this.setState({ currentTime: newValue })
    }

    // Handle when user stops seeking
    handleSeekCommitted = (event, newValue) => {
        this.videoRef.current.currentTime = newValue
        this.setState({ seeking: false })
    }

    handlePlayPause = () => {
        const video = this.videoRef.current
        if (this.state.isPlaying) {
            video.pause()
        } else {
            video.play()
        }
        this.setState({ isPlaying: !this.state.isPlaying })
    }

    handleSkipTime = (time) => {
        this.setState({ currentTime: this.state.currentTime + time })
    }

    handleMuteUnmute = () => {
        const video = this.videoRef.current

        this.setState({ muted: !this.state.muted }, () => {
            video.muted = this.state.muted
        })
    }

    handleVolumeChange = (e, newVolume) => {
        this.setState({ volume: newVolume })
        this.videoRef.current.volume = newVolume / 100
    }

    handleFullScreen = () => {
        if (this.videoRef.current.requestFullscreen) {
            this.videoRef.current.requestFullscreen()
        }
    }

    formatTime = (time) => {
        const minutes = Math.floor(time / 60)
        const seconds = Math.floor(time % 60)
        return `${minutes}:${seconds < 10 ? `0${seconds}` : seconds}`
    }

    render() {
        const { videoUrl, isPlaying, volume, muted, currentTime, duration } =
            this.state

        return (
            <Box className="video-player-container">
                {/*  Video Player  */}
                <video
                    ref={this.videoRef}
                    onTimeUpdate={this.handleTimeUpdate}
                    autoPlay
                    width="100%"
                >
                    <source src={videoUrl} type="video/mp4" />
                </video>
                <Slider
                    sx={{ padding: '0' }}
                    value={currentTime}
                    max={duration}
                    step={0.1}
                    onChange={this.handleSeekChange}
                    onChangeCommitted={this.handleSeekCommitted}
                />

                {/* Controls */}
                <Box className="control-container">
                    <Box className="control-content">
                        {/* Play/Pause */}
                        <IconButton
                            className="icon-button"
                            onClick={this.handlePlayPause}
                        >
                            {!isPlaying || currentTime === duration ? (
                                <Icon icon={'play'} />
                            ) : (
                                <Icon icon={'pause'} />
                            )}
                        </IconButton>
                        {/* Forward and backward */}
                        <IconButton className="icon-button">
                            <Icon icon={'left_arrow2'} />
                        </IconButton>
                        <IconButton
                            className="icon-button"
                            onClick={() => {
                                this.handleSkipTime(5)
                            }}
                        >
                            <Icon icon={'right_arrow2'} />
                        </IconButton>
                        {/* Volume */}
                        <IconButton
                            className="icon-button"
                            onClick={this.handleMuteUnmute}
                        >
                            {volume === 0 || muted ? (
                                <Icon icon={'mute'} />
                            ) : (
                                <Icon icon={'speaker'} />
                            )}
                        </IconButton>

                        <Slider
                            value={volume}
                            onChange={this.handleVolumeChange}
                            sx={{ width: 100 }}
                        />
                    </Box>

                    <Box className="control-content">
                        {/*  Current time  */}
                        <Typography className="video-time">
                            {this.formatTime(currentTime)}
                            {' / '}
                            {this.formatTime(duration)}
                        </Typography>
                        <IconButton className="icon-button">
                            <Icon icon={'setting'} />
                        </IconButton>
                        <IconButton
                            className="icon-button"
                            onClick={() => {
                                this.handleFullScreen()
                            }}
                        >
                            <Icon icon={'square_frame_workbench'} />
                        </IconButton>
                    </Box>
                </Box>
            </Box>
        )
    }
}

export default VideoPlayer
