import React, { Component } from 'react'

import { Box, IconButton, Slider, Typography } from '@mui/material'

import Icon from '../../../IconLibrary/Icon'

import './VideoPlayer.css'

class VideoPlayer extends Component {
    constructor(props) {
        super(props)
        this.state = {
            videoUrl: 'https://www.w3schools.com/html/mov_bbb.mp4',
            isPlaying: true,
            volume: 50,
            playbackRate: 1,
            currentTime: 0,
        }
    }

    render() {
        return (
            <Box
                sx={{
                    width: '100%',
                    maxWidth: '887px',
                    margin: '16px 0',
                    minWidth: '600px',
                    backgroundColor: 'black',
                }}
            >
                {/*  Video Player  */}
                <video autoPlay width="100%">
                    <source src={this.state.videoUrl} type="video/mp4" />
                </video>

                {/* Controls */}
                <Box
                    sx={{
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'space-between',
                    }}
                    className="control-container"
                >
                    {/*Play/Pause*/}
                    <IconButton className="icon-button">
                        {this.state.isPlaying ? (
                            <Icon icon={'play'} />
                        ) : (
                            <Icon icon={'pause'} />
                        )}
                    </IconButton>
                    {/* Forward and backward */}
                    <IconButton className="icon-button">
                        <Icon icon={'double_forward'} />
                    </IconButton>
                    <IconButton className="icon-button">
                        <Icon icon={'double_backward'} />
                    </IconButton>
                    {/* Volume */}
                    {this.state.volume === 0 ? (
                        <IconButton className="icon-button">
                            <Icon icon={'mute'} />
                        </IconButton>
                    ) : (
                        <IconButton className="icon-button">
                            <Icon icon={'mute'} />
                        </IconButton>
                    )}
                    <Slider value={this.state.volume} sx={{ width: 100 }} />

                    {/*  Current time  */}
                    <Typography className="video-time">
                        {this.state.currentTime}
                    </Typography>
                    <IconButton className="icon-button">
                        <Icon icon={'setting'} />
                    </IconButton>
                    <IconButton className="icon-button">
                        <Icon icon={'square_frame_workbench'} />
                    </IconButton>
                </Box>
            </Box>
        )
    }
}

export default VideoPlayer
