import React, { Component } from 'react'

import { Box, IconButton, Slider, Typography } from '@mui/material'

import Icon from '../../../IconLibrary/Icon'

import './VideoPlayer.css'

class VideoPlayer extends Component {
    constructor(props) {
        super(props)
        this.state = {
            videoUrl: 'https://www.w3schools.com/html/mov_bbb.mp4',
            isPlaying: true,
            volume: 50,
            playbackRate: 1,
            currentTime: 0,
            duration: 0,
        }
        this.videoRef = React.createRef()
    }

    handlePlayPause = () => {
        if (this.state.isPlaying) {
            this.videoRef.current.pause()
        } else {
            this.videoRef.current.play()
        }
        this.setState({ ...this.state, isPlaying: !this.state.isPlaying })
    }

    handleTimeUpdate = () => {
        this.setState({
            ...this.state,
            currentTime: this.videoRef.current.currentTime,
        })
    }

    handleFullScreen = () => {
        if (this.videoRef.current.requestFullscreen) {
            this.videoRef.current.requestFullscreen()
        }
    }

    formatTime = (time) => {
        const minutes = Math.floor(time / 60)
        const seconds = Math.floor(time % 60)
        return `${minutes}:${seconds < 10 ? `0${seconds}` : seconds}`
    }

    componentDidMount() {
        this.setState({
            ...this.state,
            duration: this.videoRef.current.duration,
        })
        console.log(this.videoRef.current)
    }

    render() {
        return (
            <Box className="video-player-container">
                {/*  Video Player  */}
                <video
                    ref={this.videoRef}
                    onTimeUpdate={this.handleTimeUpdate}
                    autoPlay
                    width="100%"
                >
                    <source src={this.state.videoUrl} type="video/mp4" />
                </video>
                <Slider sx={{ padding: '0' }} />

                {/* Controls */}
                <Box className="control-container">
                    <Box className="control-content">
                        {/* Play/Pause */}
                        <IconButton
                            className="icon-button"
                            onClick={this.handlePlayPause}
                        >
                            {!this.state.isPlaying ? (
                                <Icon icon={'play'} />
                            ) : (
                                <Icon icon={'pause'} />
                            )}
                        </IconButton>
                        {/* Forward and backward */}
                        <IconButton className="icon-button">
                            <Icon icon={'double_forward'} />
                        </IconButton>
                        <IconButton className="icon-button">
                            <Icon icon={'double_backward'} />
                        </IconButton>
                        {/* Volume */}
                        {this.state.volume === 0 ? (
                            <IconButton className="icon-button">
                                <Icon icon={'mute'} />
                            </IconButton>
                        ) : (
                            <IconButton className="icon-button">
                                <Icon icon={'mute'} />
                            </IconButton>
                        )}
                        <Slider value={this.state.volume} sx={{ width: 100 }} />
                    </Box>

                    <Box className="control-content">
                        {/*  Current time  */}
                        <Typography className="video-time">
                            {this.formatTime(this.state.currentTime)}
                            {' / '}
                            {this.formatTime(this.state.duration)}
                        </Typography>
                        <IconButton className="icon-button">
                            <Icon icon={'setting'} />
                        </IconButton>
                        <IconButton
                            className="icon-button"
                            onClick={() => {
                                this.handleFullScreen()
                            }}
                        >
                            <Icon icon={'square_frame_workbench'} />
                        </IconButton>
                    </Box>
                </Box>
            </Box>
        )
    }
}

export default VideoPlayer
