import React, { Component } from 'react'
import Icon from '../../../IconLibrary/Icon'
import ExpandableLayout from '../../CommonComponents/Layout/ExpandableLayout'
import PropTypes from 'prop-types'
import { DataFileUpload } from './Data'

class DataUploadSidebar extends Component {
    constructor(props) {
        super(props)
        this.state = {}
    }

    // handleRadioChange = (event) => {
    //     this.setState({
    //         selectedDataFileUpload: this.state.dataFileUpload.find(
    //             (item) => item.date === event.target.value
    //         ),
    //     })
    // }
    //
    // handleSortChange = (event) => {
    //     this.setState({
    //         sortType: this.state.sortType === 'asc' ? 'desc' : 'asc',
    //         dataFileUpload: this.state.dataFileUpload.reverse(),
    //     })
    //     console.log(event.target.value)
    // }

    render() {
        const {
            sortType,
            dataFileUpload,
            selectedDataFileUpload,
            handleRadioChange,
            handleSortChange,
        } = this.props

        return (
            <ExpandableLayout backgroundColor={'white'} width={'200px'}>
                <div className="data-upload-history">
                    <div className="data-upload-header">
                        <span className="data-upload-header-title">
                            Data Uploading Date
                        </span>
                        <div className="data-upload-header-action">
                            <button
                                className="data-upload-header-action"
                                onClick={handleSortChange}
                            >
                                <Icon
                                    icon={
                                        sortType === 'asc'
                                            ? 'sort_down1'
                                            : 'sort_up1'
                                    }
                                />
                            </button>
                        </div>
                    </div>
                    <div className="data-upload-history-list">
                        {dataFileUpload.map((item, index) => (
                            <label
                                key={index}
                                className={`radio-item ${selectedDataFileUpload?.date === item.date ? 'selected' : ''}`}
                            >
                                <input
                                    type="radio"
                                    value={item.date}
                                    checked={
                                        selectedDataFileUpload?.date ===
                                        item.date
                                    }
                                    onChange={handleRadioChange}
                                />
                                <span>{item.date}</span>
                                <span>{item.time}</span>
                            </label>
                        ))}
                    </div>
                </div>
            </ExpandableLayout>
        )
    }
}

