import React, { Component } from 'react'
import SearchBar from '../../CommonComponents/DefaultSearchComponent/SearchBar'
import MyTable from '../../CommonComponents/MyTable/MyTable'
import {
    DefaultActionProps,
    DefaultCellProps,
} from '../../CommonComponents/MyTable/TableProps'
import Pagination from '../../CommonComponents/BasicComponents/PaginationV1/Pagination'
import { Headers } from './TableProps'
import { Data } from './DefaultProps'
import './TrainingData.css'

class TrainingData extends Component {
    constructor(props) {
        super(props)
        this.state = {
            header: [],
            data: [],
            perPage: 5,
            totalPage: 0,
            currentPage: 1,
            dataPerPage: [],
        }
    }

    componentDidMount() {
        this.setState({
            header: Headers,
            data: Data,
            totalPage: Data.length / this.state.perPage,
            dataPerPage: Data.slice(0, this.state.perPage),
        })
    }

    handlePageChange = (pageNumber) => {
        this.setState({
            currentPage: pageNumber,
            dataPerPage: this.state.data.slice(
                pageNumber * this.state.perPage - this.state.perPage,
                pageNumber * this.state.perPage
            ),
        })
    }

    render() {
        const { header, data, perPage, currentPage, totalPage, dataPerPage } =
            this.state
        return (
            <div className="container">
                <div className="header-container">
                    <span>
                        Assignee: <span className="special-text">AnyGen</span>
                    </span>
                    <div style={{ display: 'flex', alignItems: 'center' }}>
                        <span>
                            Total patents:{' '}
                            <span className="special-text">{data.length}</span>
                        </span>
                        <SearchBar />
                    </div>
                </div>
                <div className="table-container">
                    <MyTable
                        Headers={header}
                        Data={dataPerPage}
                        autoHeight={true}
                        cellHeight="100%"
                        noShadow={true}
                    />
                    <br />
                    <Pagination
                        paginationType={'both'}
                        noOfPages={totalPage}
                        onChangePage={this.handlePageChange}
                        currentPage={currentPage}
                        pageItemsLimit={perPage}
                    />
                </div>
            </div>
        )
    }
}

export default TrainingData
