import React, { Component } from 'react'
import Icon from '../../../IconLibrary/Icon'
import ExpandableLayout from '../../CommonComponents/Layout/ExpandableLayout'
import { DataFileUpload } from '../AdditionalData/Data'

class DataUploadSidebar extends Component {
    constructor(props) {
        super(props)

        this.state = {
            sortType: '',
            dataFileUpload: [],
            selectedDataFileUpload: {},
        }
    }

    componentDidMount() {
        this.setState({
            sortType: this.props.sortType,
            dataFileUpload: this.props.dataFileUpload,
            selectedDataFileUpload: this.props.selectedDataFileUpload,
        })
    }

    componentDidUpdate(prevProps, prevState, snapshot) {
        if (
            prevProps.dataFileUpload !== this.props.dataFileUpload ||
            prevProps.selectedDataFileUpload !==
                this.props.selectedDataFileUpload ||
            this.state.sortType !== prevProps.sortType
        ) {
            this.setState({
                sortType: this.props.sortType,
                dataFileUpload: this.props.dataFileUpload,
                selectedDataFileUpload: this.props.selectedDataFileUpload,
            })
        }
    }

    render() {
        const { sortType, dataFileUpload, selectedDataFileUpload } = this.state

        const { handleRadioChange, handleSortChange } = this.props

        return (
            <ExpandableLayout backgroundColor={'white'} width={'200px'}>
                <div className="data-upload-history">
                    <div className="data-upload-header">
                        <span className="data-upload-header-title">
                            Data Uploading Date
                        </span>
                        <div className="data-upload-header-action">
                            <button
                                className="data-upload-header-action"
                                onClick={handleSortChange}
                            >
                                <Icon
                                    icon={
                                        sortType === 'asc'
                                            ? 'sort_down1'
                                            : 'sort_up1'
                                    }
                                />
                            </button>
                        </div>
                    </div>
                    <div className="data-upload-history-list">
                        {dataFileUpload.map((item, index) => (
                            <label
                                key={index}
                                className={`radio-item ${selectedDataFileUpload?.date === item.date ? 'selected' : ''}`}
                            >
                                <input
                                    type="radio"
                                    value={item.date}
                                    checked={
                                        selectedDataFileUpload?.date ===
                                        item.date
                                    }
                                    onChange={handleRadioChange}
                                />
                                <span>{item.date}</span>
                                <span>{item.time}</span>
                            </label>
                        ))}
                    </div>
                </div>
            </ExpandableLayout>
        )
    }
}

export default DataUploadSidebar
