import React, { Component } from 'react'
import TabPopup from '../BasicComponents/Tab/TabPopup'
import Nodes from '../Nodes/Nodes'
import './ClassificationTree.css'
import NodesActions from '../Nodes/NodesActions/NodesActions'

import { Headers } from '../Nodes/TableProps'
import { Data } from '../Nodes/DefaultProps'
import Pagination from '../../CommonComponents/BasicComponents/PaginationV1/Pagination'

class ClassificationTree extends Component {
    constructor(props) {
        super(props)
        this.state = {
            filterData: Headers,
            data: Data,
            filteredData: Data,
            typingTimeOut: 0,
            sortOrder: 'asc',
            dropDownFilterList: [],
            dropDownFilterValue: null,
            totalPage: 0,
            perPage: 5,
            currentPage: 1,
            dataPerPage: [],
        }
    }

    componentDidMount() {
        let dropDownList = []
        for (let i = 0; i < this.state.filterData.length; i++) {
            const { DisplayName, Key, ...item } = {
                ...this.state.filterData[i],
            }
            dropDownList.push({ label: DisplayName, value: Key })
        }
        this.setState({
            // filterData: this.props.filterData,
            dropDownFilterList: dropDownList,
            dropDownFilterValue: dropDownList[0],
            totalPage: Math.ceil(Data.length / this.state.perPage),
        })
    }

    handleSearch = (event) => {
        const searchTerm = event.target.value.toLowerCase()

        if (this.state.typingTimeOut) {
            clearTimeout(this.state.typingTimeOut)
        }

        this.setState({
            typingTimeOut: setTimeout(() => {
                const filteredData = this.state.data.filter((item) => {
                    const idMatch = item.id.toLowerCase().includes(searchTerm)
                    const nameMatch = item.name
                        .toLowerCase()
                        .includes(searchTerm)
                    const descriptionMatch = item.description
                        .toLowerCase()
                        .includes(searchTerm)

                    return idMatch || nameMatch || descriptionMatch
                })

                this.setState({ filteredData })
                // this.handlePageChange(1)
                // console.log(filteredData)
            }, 500),
        })
    }

    onSortOrder = () => {
        const filteredData = this.state.filteredData.reverse()
        this.setState({
            sortOrder: this.state.sortOrder === 'asc' ? 'desc' : 'asc',
            filteredData,
        })
    }

    handleDropDownFilterChange = (value) => {
        const filteredData = this.state.filteredData?.sort((a, b) => {
            //     if (value.value === 'id')
            //         return a[value.value].PatentId.localeCompare(
            //             b[value.value].PatentId
            //         )
            //     else if (value.value === 'class')
            //         return a[value.value].length - b[value.value].length
            //     else
            return a[value.value].localeCompare(b[value.value])
        })
        if (this.state.sortOrder === 'desc') filteredData.reverse()
        this.setState({
            dropDownFilterValue: value,
            filteredData,
        })
        // this.handlePageChange(1)
    }

    handlePageChange = (pageNumber) => {
        this.setState({
            currentPage: pageNumber,
            dataPerPage: this.state.filteredData.slice(
                pageNumber * this.state.perPage - this.state.perPage,
                pageNumber * this.state.perPage
            ),
            totalPage: Math.ceil(
                this.state.filteredData.length / this.state.perPage
            ),
        })
    }

    render() {
        const tabs = [
            {
                title: 'Nodes',
                element: <Nodes filteredData={this.state.filteredData} />,
            },
            {
                title: 'Tree',
                element: <></>,
            },
        ]

        const { totalPage, currentPage, perPage } = this.state
        return (
            <div className="container">
                <TabPopup
                    tabs={tabs}
                    actions={
                        <NodesActions
                            filterData={this.state.filterData}
                            onSearch={this.handleSearch}
                            sortOrder={this.state.sortOrder}
                            onSortOrder={this.onSortOrder}
                            handleDropDownFilterChange={
                                this.handleDropDownFilterChange
                            }
                            dropDownFilterValue={this.state.dropDownFilterValue}
                            dropDownFilterList={this.state.dropDownFilterList}
                        />
                    }
                    tabFontSize={12}
                />
                <br />
                {totalPage > 1 && (
                    <Pagination
                        paginationType={'both'}
                        noOfPages={totalPage}
                        onChangePage={this.handlePageChange}
                        currentPage={currentPage}
                        pageItemsLimit={perPage}
                    />
                )}
            </div>
        )
    }
}

export default ClassificationTree
