import React, { Component } from 'react'

import { Box, Tab } from '@mui/material'
import { TabContext, TabList, TabPanel } from '@mui/lab'

import TabBar from '../../../CommonComponents/BasicComponents/TabBar/TabBar'

import './TabPopup.css'
import PropTypes from 'prop-types'
import { DataFileUpload } from '../../AdditionalData/Data'

class TabPopup extends Component {
    constructor(props) {
        super(props)
        this.state = {
            value: '0',
            activeTabName: {},
        }
    }

    componentDidMount() {
        this.setState({
            activeTabName: this.props.tabs[0],
        })
    }
    render() {
        const { tabs, actions, tabFontSize } = this.props

        const handleChange = (event, newValue) => {
            this.setState({ value: newValue })
        }

        return (
            <TabContext value={this.state.value}>
                <Box
                    sx={{
                        borderBottom: 1,
                        borderColor: '#cdcdcd',
                        display: 'flex',
                        justifyContent: 'space-between',
                        alignItems: 'center',
                    }}
                >
                    {/*<TabList*/}
                    {/*    onChange={handleChange}*/}
                    {/*    TabIndicatorProps={{*/}
                    {/*        style: {*/}
                    {/*            backgroundColor: '#000',*/}
                    {/*        },*/}
                    {/*    }}*/}
                    {/*    className="tab-list"*/}
                    {/*>*/}
                    {/*    {tabs &&*/}
                    {/*        tabs.map((item, index) => (*/}
                    {/*            <Tab*/}
                    {/*                key={index}*/}
                    {/*                label={item.title}*/}
                    {/*                value={index.toString()}*/}
                    {/*                className="tab"*/}
                    {/*                sx={{*/}
                    {/*                    fontSize: tabFontSize*/}
                    {/*                        ? tabFontSize + 'px'*/}
                    {/*                        : '14px',*/}
                    {/*                }}*/}
                    {/*            />*/}
                    {/*        ))}*/}
                    {/*</TabList>*/}
                    <TabBar
                        tabList={tabs}
                        activeTab={this.state.activeTabName}
                    />
                    <Box>{actions}</Box>
                </Box>
                <Box
                    sx={{
                        display: 'flex',
                        flexDirection: 'column',
                        height: '100%',
                        flex: 1,
                    }}
                >
                    {tabs &&
                        tabs.map((item, index) =>
                            this.state.value === index.toString() ? (
                                <TabPanel
                                    key={index}
                                    value={index.toString()}
                                    sx={{
                                        padding: '0',
                                        height: '100%',
                                        display: 'flex',
                                        // flexGrow: 1,
                                    }}
                                    className="tab-content"
                                >
                                    {item.element}
                                </TabPanel>
                            ) : null
                        )}
                </Box>
            </TabContext>
        )
    }
}

TabPanel.propTypes = {
    tabs: PropTypes.array.isRequired, //[{element, title}]
    actions: PropTypes.element,
    tabFontSize: PropTypes.number,
}

export default TabPopup
