import React, { Component } from 'react'

import Pagination from '../../CommonComponents/BasicComponents/PaginationV1/Pagination'

import MyTable from '../BasicComponents/PopupTable/MyTable'
import SearchBox from '../BasicComponents/SearchBox/SearchBox'
import DropDown from '../BasicComponents/DropDown/DropDown'
import { Headers } from './TableProps'
import { Data } from './DefaultProps'
import './TrainingData.css'
import Icon from '../../../IconLibrary/Icon'
import MyCheckDrop from '../../CommonComponents/BasicComponents/MyDropDown/MyCheckDrop'
// import TypeBasedSort from '../../CommonComponents/DefaultSearchComponent/TypeBasedSort'
import TypeBasedSort from '../BasicComponents/SearchComponent/TypeBasedSort'

class TrainingData extends Component {
    constructor(props) {
        super(props)
        this.state = {
            header: [],
            data: [],
            perPage: 5,
            totalPage: 0,
            currentPage: 1,
            dataPerPage: [],
            dropDownList: [],
            searchValue: '',
            filteredData: Data,
            typingTimeOut: 0,
            selectAllItems: true,
            sortBy: '',
            sortOrder: 'asc',
        }
    }

    componentDidMount() {
        let dropDownList = []
        for (let i = 0; i < Headers.length; i++) {
            const { DisplayName, Key, Visible, ...item } = { ...Headers[i] }
            dropDownList.push({
                Name: DisplayName,
                value: Key,
                isSelected: true,
                Visible: Visible === 'always' ? Visible : 'none',
            })
        }
        this.setState({
            header: Headers,
            data: Data,
            totalPage: Math.ceil(Data.length / this.state.perPage),
            dataPerPage: Data.slice(0, this.state.perPage),
            dropDownViewValue: this.state.dropDownList[0],
            dropDownFilterList: dropDownList,
            dropDownFilterValue: dropDownList[0],
            dropDownList,
            sortOrder: 'asc',
            sortBy: dropDownList[0],
        })
    }

    componentDidUpdate(prevProps, prevState, snapshot) {
        // this.setState({
        //     totalPage: Math.ceil(this.state.data.length / this.state.perPage),
        // })
    }

    handleDropDownViewChange = (value) => {
        this.setState({ dropDownViewValue: value })
    }

    handleDropDownFilterChange = (value) => {
        const filteredData = this.state.filteredData?.sort((a, b) => {
            if (value.value === 'id')
                return a[value.value].PatentId.localeCompare(
                    b[value.value].PatentId
                )
            else if (value.value === 'class')
                return a[value.value].length - b[value.value].length
            else return a[value.value].localeCompare(b[value.value])
        })
        // console.log(value)
        if (this.state.sortOrder === 'desc') filteredData.reverse()
        this.setState({ dropDownFilterValue: value, filterData: filteredData })
        this.handlePageChange(1)
    }

    handlePageChange = (pageNumber) => {
        this.setState({
            currentPage: pageNumber,
            dataPerPage: this.state.filteredData.slice(
                pageNumber * this.state.perPage - this.state.perPage,
                pageNumber * this.state.perPage
            ),
            totalPage: Math.ceil(
                this.state.filteredData.length / this.state.perPage
            ),
        })
    }

    handleSearch = (event) => {
        const searchTerm = event.target.value.toLowerCase()

        if (this.state.typingTimeOut) {
            clearTimeout(this.state.typingTimeOut)
        }

        this.setState({
            searchValue: event.target.value,
            typingTimeOut: setTimeout(() => {
                const filteredData = this.state.filteredData.filter((item) => {
                    // Check for search term in id.PatentId, title, abstract, and class.Name
                    const patentIdMatch =
                        item.PatentId.toLowerCase().includes(searchTerm)
                    const titleMatch =
                        item.Title.toLowerCase().includes(searchTerm)
                    const abstractMatch =
                        item.Abstract.toLowerCase().includes(searchTerm)
                    const classMatch =
                        item.Class.toLowerCase().includes(searchTerm)

                    return (
                        patentIdMatch ||
                        titleMatch ||
                        abstractMatch ||
                        classMatch
                    )
                })

                this.setState({ filteredData })
                this.handlePageChange(1)
            }, 500),
        })
    }

    onChangeSelectAll = () => {
        // const nextValues = [...this.state.dropDownList]
        // if (this.state.selectAllItems) {
        //     nextValues.map((item, index) => {
        //         if (item.Visible !== 'always') item.isSelected = false
        //     })
        // } else {
        //     nextValues.map((item, index) => {
        //         if (item.Visible !== 'always') item.isSelected = true
        //     })
        // }
        // this.setState({
        //     selectAllItems: !this.state.selectAllItems,
        //     values: nextValues,
        // })
    }

    onChangeSelectItem = (index, item) => {
        if (item.Visible === 'always') return

        const nextValues = [...this.state.dropDownList]
        nextValues[index].isSelected = !nextValues[index].isSelected

        const nextHeaders = Headers.filter((item, index) => {
            return nextValues[index].isSelected
        })

        this.setState({
            dropDownList: nextValues,
            selectAllItems:
                nextValues.length ===
                nextValues.filter((item) => item.isSelected).length,
            header: nextHeaders,
        })
    }

    onSortOrder = () => {
        const filteredData = this.state.filteredData.reverse()
        this.setState({
            sortOrder: this.state.sortOrder === 'asc' ? 'desc' : 'asc',
            filteredData,
        })
        this.handlePageChange(1)
    }

    onSortBy = (sortBy) => {
        const nextSortBy = this.state.dropDownList.find(
            (i) => i.Name === sortBy
        )
        const nextValues = this.state.filteredData.sort((a, b) => {
            return a[nextSortBy.value].localeCompare(b[nextSortBy.value])
        })
        this.setState({
            sortBy: nextSortBy,
            filteredData:
                this.state.sortOrder === 'asc'
                    ? nextValues
                    : nextValues.reverse(),
        })
        this.handlePageChange(1)
    }

    render() {
        const {
            header,
            data,
            perPage,
            currentPage,
            totalPage,
            dataPerPage,
            dropDownList,
            dropDownViewValue,
            dropDownFilterList,
            dropDownFilterValue,
            searchValue,
            filteredData,
            sortOrder,
            selectAllItems,
            sortBy,
        } = this.state

        return (
            <div className="container">
                <div className="header-container">
                    <span>
                        Assignee: <span className="special-text">AnyGen</span>
                    </span>
                    <div style={{ display: 'flex', alignItems: 'center' }}>
                        <span
                            className="mr8"
                            style={{
                                width: '100%',
                            }}
                        >
                            Total patents:{' '}
                            <span className="special-text">
                                {filteredData.length}
                            </span>
                        </span>
                        <div className="mr8">
                            <SearchBox
                                width="200px"
                                value={searchValue}
                                onSearch={this.handleSearch}
                            />
                        </div>
                        {/*<DropDown*/}
                        {/*    className="drop-down mr8"*/}
                        {/*    onChange={this.handleDropDownViewChange}*/}
                        {/*    list={dropDownList}*/}
                        {/*    value={dropDownViewValue}*/}
                        {/*/>*/}
                        <MyCheckDrop
                            className="mr8"
                            title={'Columns to view'}
                            width={'fit-content'}
                            values={dropDownList}
                            selectAllItems={selectAllItems}
                            onChangeSelectAll={this.onChangeSelectAll}
                            onChangeSelectItem={this.onChangeSelectItem}
                            onChange={this.onChangeSelectItem}
                            // selectedCount={() => {
                            //     return dropDownList.filter(
                            //         (item) => item.isSelected
                            //     ).length
                            // }}
                        />
                        <TypeBasedSort
                            // width={'max-content'}
                            sortByOptions={dropDownList}
                            sortBy={sortBy.Name}
                            sortOrder={sortOrder}
                            onChange={(sortBy) => this.onSortBy(sortBy)}
                            onSortOrderChange={this.onSortOrder}
                        />
                        {/*<button*/}
                        {/*    className="menu-button"*/}
                        {/*    onClick={this.onSortOrder}*/}
                        {/*>*/}
                        {/*    <Icon*/}
                        {/*        icon={*/}
                        {/*            sortOrder === 'asc'*/}
                        {/*                ? 'sort_up'*/}
                        {/*                : 'sort_down'*/}
                        {/*        }*/}
                        {/*        size={14}*/}
                        {/*    />*/}
                        {/*</button>*/}
                        {/*<DropDown*/}
                        {/*    className="drop-down"*/}
                        {/*    // icon="sort_down1"*/}
                        {/*    // iconColor="black"*/}
                        {/*    onChange={this.handleDropDownFilterChange}*/}
                        {/*    list={dropDownFilterList}*/}
                        {/*    value={dropDownFilterValue}*/}
                        {/*/>*/}
                    </div>
                </div>
                <div
                    className="table-container"
                    style={{
                        flexGrow: '1',
                    }}
                >
                    <MyTable Headers={header} Data={dataPerPage} />
                    <br />
                </div>
                {totalPage > 1 && (
                    <Pagination
                        paginationType={'both'}
                        noOfPages={totalPage}
                        onChangePage={this.handlePageChange}
                        currentPage={currentPage}
                        pageItemsLimit={perPage}
                    />
                )}
            </div>
        )
    }
}

export default TrainingData
