import React, { Component } from 'react'

import SearchBar from '../../CommonComponents/DefaultSearchComponent/SearchBar'
import MyTable from '../../CommonComponents/MyTable/MyTable'
import Pagination from '../../CommonComponents/BasicComponents/PaginationV1/Pagination'

import SearchBox from '../../PopupComponents/BasicComponents/SearchBox/SearchBox'
import { Headers } from './TableProps'
import { Data } from './DefaultProps'
import './TrainingData.css'
import DropDown from '../../CommonComponents/BasicComponents/ComboBox/DropDown'

class TrainingData extends Component {
    constructor(props) {
        super(props)
        this.state = {
            header: [],
            data: [],
            perPage: 5,
            totalPage: 0,
            currentPage: 1,
            dataPerPage: [],
            dropDownViewList: [
                { label: 'Columns to view', value: 'col' },
                { label: 'Rows to view', value: 'row' },
            ],
            dropDownViewValue: null,
            dropDownFilterList: [],
            dropDownFilterValue: null,
        }
    }

    componentDidMount() {
        this.setState({
            header: Headers,
            data: Data,
            totalPage: Data.length / this.state.perPage,
            dataPerPage: Data.slice(0, this.state.perPage),
            dropDownViewValue: this.state.dropDownViewList[0],
        })
    }

    handleDropDownViewChange = (value) => {
        this.setState({ dropDownViewValue: value })
    }

    handlePageChange = (pageNumber) => {
        this.setState({
            currentPage: pageNumber,
            dataPerPage: this.state.data.slice(
                pageNumber * this.state.perPage - this.state.perPage,
                pageNumber * this.state.perPage
            ),
        })
    }

    render() {
        const {
            header,
            data,
            perPage,
            currentPage,
            totalPage,
            dataPerPage,
            dropDownViewList,
            dropDownViewValue,
            dropDownFilterList,
        } = this.state

        return (
            <div className="container">
                <div className="header-container">
                    <span>
                        Assignee: <span className="special-text">AnyGen</span>
                    </span>
                    <div style={{ display: 'flex', alignItems: 'center' }}>
                        <span>
                            Total patents:{' '}
                            <span className="special-text">{data.length}</span>
                        </span>
                        <SearchBox width="200px" />
                        <DropDown
                            onChange={this.handleDropDownViewChange}
                            list={dropDownViewList}
                            value={dropDownViewValue}
                        />
                        {/*<SearchBar*/}
                        {/*    searchBarType={'default'}*/}
                        {/*    sort={false}*/}
                        {/*    filter={false}*/}
                        {/*/>*/}
                    </div>
                </div>
                <div className="table-container">
                    <MyTable
                        Headers={header}
                        Data={dataPerPage}
                        autoHeight={true}
                        cellHeight="100%"
                        noShadow={true}
                    />
                    <br />
                    <Pagination
                        paginationType={'both'}
                        noOfPages={totalPage}
                        onChangePage={this.handlePageChange}
                        currentPage={currentPage}
                        pageItemsLimit={perPage}
                    />
                </div>
            </div>
        )
    }
}

export default TrainingData
